import React from 'react';
import { Link } from 'react-router-dom';
import './Item.css';

const Item = (props) => {
    return (
        <div className={`item-container ${props.type}`} style={props.color ? { background: props.color } : {}}>
            <Link to={`/product/${props.id}`}>
                <div className="item">
                    <div className="item-img">
                        <img onClick={window.scrollTo(0,0)} src={props.image} alt="" />
                    </div>


                    <p className='character-limit'>{props.name}</p>
                    <div className='item-name'>
                        {props.type === 'category' && (
                            <p className='character-limit'>{props.name}</p>
                        )}
                        {props.type === 'trendy' && (
                            <div className="item-price-wrapper">
                                <div className="item-price">
                                    {props.current_price} đ
                                </div>
                                <div className="previous">
                                    {props.previous_price} đ
                                </div>
                            </div>
                        )}
                    </div>  


                    <p className='item-subtitle character-limit'>{props.subtitle}</p>
                    <div className="item-prices">
                        {props.type === 'category' && (
                            <div className="item-price-sale">
                                -{props.discount}% <p>off</p>
                            </div>
                        )}
                        {props.type === 'trendy' && (
                            <div className="item-price-wrapper">
                                <div className="item-price">
                                    {props.current_price} đ
                                </div>
                                <div className="previous">
                                    {props.previous_price} đ
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </Link>
        </div>
    );
};

export default Item;
