import React, { useState, useEffect, useRef } from 'react';
import { ChevronRight, ChevronLeft, TrendingUp } from "lucide-react";
import data_product from '../Assets/TrendingTech/data';
import TrendingTechItem from '../TrendingTechItem/TrendingTechItem';
import './TrendingTech.css';

const TrendingTech = () => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const [isTransitioning, setIsTransitioning] = useState(false);
    const sliderWrapperRef = useRef(null);
    const slideRefs = useRef([]);
    const autoSlideInterval = useRef(null);

    const totalSlides = data_product.length;

    const moveToNextSlide = () => {
        if (!isTransitioning) {
            setIsTransitioning(true);
            setCurrentIndex((prevIndex) => (prevIndex + 1) % totalSlides);
        }
    };

    const moveToPrevSlide = () => {
        if (!isTransitioning) {
            setIsTransitioning(true);
            setCurrentIndex((prevIndex) => (prevIndex - 1 + totalSlides) % totalSlides);
        }
    };

    const startAutoSlide = () => {
        autoSlideInterval.current = setInterval(moveToNextSlide, 5000);
    };

    const stopAutoSlide = () => {
        if (autoSlideInterval.current) {
            clearInterval(autoSlideInterval.current);
        }
    };

    useEffect(() => {
        const updateSlider = () => {
            if (slideRefs.current.length > 0 && sliderWrapperRef.current) {
                const slideWidth = slideRefs.current[0].clientWidth;
                sliderWrapperRef.current.style.transform = `translateX(-${currentIndex * slideWidth}px)`;
            }
        };

        const handleTransitionEnd = () => {
            setIsTransitioning(false);
        };

        updateSlider();
        sliderWrapperRef.current.addEventListener('transitionend', handleTransitionEnd);
        window.addEventListener('resize', updateSlider);

        startAutoSlide();

        return () => {
            if (sliderWrapperRef.current) {
                sliderWrapperRef.current.removeEventListener('transitionend', handleTransitionEnd);
            }
            window.removeEventListener('resize', updateSlider);
            stopAutoSlide();
        };
    }, [currentIndex]);

    return (
        <div className="offers" onMouseEnter={stopAutoSlide} onMouseLeave={startAutoSlide}>
            <div className="offers-top offers-shared">
                <div className="offers-top-leftt offers-shared">
                    <TrendingUp />
                    <h1>Trending Tech</h1>
                </div>
                <div className="offers-top-right offers-shared">
                    <div className="show-all offers-shared">
                        <h1>Show All</h1>
                        <p>{totalSlides}</p>
                        <ChevronRight />
                    </div>
                    <div className="navigation-button offers-shared">
                        <div className="navigation-item offers-shared" onClick={moveToPrevSlide}>
                            <ChevronLeft />
                        </div>
                        <div className="navigation-item offers-shared" onClick={moveToNextSlide}>
                            <ChevronRight />
                        </div>
                    </div>
                </div>
            </div>
            <div className="offers-bottom">
                <div className="slider-wrapper" ref={sliderWrapperRef}>
                    {data_product.map((item, i) => (
                        <TrendingTechItem
                            key={i}
                            id={item.id}
                            name={item.name}
                            subtitle={item.sub}
                            image={item.image}
                            price={item.price}
                            ref={(el) => {
                                if (el) {
                                    slideRefs.current[i] = el;
                                }
                            }}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
};

export default TrendingTech;
