import React from 'react';
import { Link } from 'react-router-dom';
import './Item.css';

const Item = (props) => {
    return (
        <div className={`item-container ${props.type}`} style={props.color ? { background: props.color } : {}}>
            <Link to={`/product/${props.id}`}>
                <div className="item">
                    <div className="item-img">
                        <img onClick={window.scrollTo(0,0)} src={props.image} alt="" />
                    </div>
                    <p>{props.name}</p>
                    <p className='item-subtitle'>{props.subtitle}</p>
                    <div className="item-prices">
                        {props.type === 'category' && (
                            <div className="item-price-sale">
                                -{props.sale}% <p>off</p>
                            </div>
                        )}
                        {props.type !== 'category' && (
                            <div className="item-price">
                                {props.price} $
                            </div>
                        )}
                    </div>
                </div>
            </Link>
        </div>
    );
};

export default Item;
