import React, { useState, useEffect, useRef } from 'react';
import { ChevronRight, ChevronLeft } from "lucide-react";
import './Slider.css';
import demoImage from '../Assets/demo.jpg';

const Slider = () => {
    const [currentIndex, setCurrentIndex] = useState(1); // Start at 1 because of the added duplicate at the start
    const [isTransitioning, setIsTransitioning] = useState(false);
    const sliderWrapperRef = useRef(null);
    const slideRefs = useRef([]);
    const autoSlideInterval = useRef(null);

    const slides = [
        { imgSrc: demoImage, title: 'Title 1', description: 'Description 1', button: 'Button 1', title2: 'Title 1-2', button2: 'Button 1-2', description2: 'Description 1-2' },
        { imgSrc: demoImage, title: 'Title 2', description: 'Description 2', button: 'Button 2', title2: 'Title 2-2', button2: 'Button 2-2', description2: 'Description 2-2' },
        { imgSrc: demoImage, title: 'Title 3', description: 'Description 3', button: 'Button 3', title2: 'Title 3-2', button2: 'Button 3-2', description2: 'Description 3-2' }
    ];

    const totalSlides = slides.length;

    const moveToNextSlide = () => {
        if (!isTransitioning) {
            setIsTransitioning(true);
            setCurrentIndex((prevIndex) => prevIndex + 1);
        }
    };

    const moveToPrevSlide = () => {
        if (!isTransitioning) {
            setIsTransitioning(true);
            setCurrentIndex((prevIndex) => prevIndex - 1);
        }
    };

    const startAutoSlide = () => {
        autoSlideInterval.current = setInterval(() => {
            moveToNextSlide();
        }, 10000); // Change slide every 18 seconds
    };

    const stopAutoSlide = () => {
        if (autoSlideInterval.current) {
            clearInterval(autoSlideInterval.current);
        }
    };

    useEffect(() => {
        const handleTransitionEnd = () => {
            if (currentIndex === 0) {
                sliderWrapperRef.current.style.transition = 'none';
                setCurrentIndex(totalSlides);
                setTimeout(() => {
                    sliderWrapperRef.current.style.transition = 'transform 0.8s cubic-bezier(0.4, 0.0, 0.2, 1)';
                    setIsTransitioning(false);
                }, 20);
            } else if (currentIndex === totalSlides + 1) {
                sliderWrapperRef.current.style.transition = 'none';
                setCurrentIndex(1);
                setTimeout(() => {
                    sliderWrapperRef.current.style.transition = 'transform 0.8s cubic-bezier(0.4, 0.0, 0.2, 1)';
                    setIsTransitioning(false);
                }, 20);
            } else {
                setIsTransitioning(false);
            }
        };

        const updateSlider = () => {
            const slideWidth = slideRefs.current[0].clientWidth;
            sliderWrapperRef.current.style.transform = `translateX(-${currentIndex * slideWidth}px)`;
        };

        updateSlider();
        sliderWrapperRef.current.addEventListener('transitionend', handleTransitionEnd);
        window.addEventListener('resize', updateSlider);

        startAutoSlide();

        return () => {
            sliderWrapperRef.current.removeEventListener('transitionend', handleTransitionEnd);
            window.removeEventListener('resize', updateSlider);
            stopAutoSlide();
        };
    }, [currentIndex, totalSlides]);

    return (
        <div className="slider" onMouseEnter={stopAutoSlide} onMouseLeave={startAutoSlide}>
            <div className="slider-container">
                <button className="nav prev" onClick={moveToPrevSlide}><ChevronLeft size={20} absoluteStrokeWidth /></button>
                <div className="slider-wrapper" ref={sliderWrapperRef}>
                    <div className="slide" ref={(el) => slideRefs.current[0] = el}>
                        <img className="bg-image" src={slides[totalSlides - 1].imgSrc} alt={`Image ${totalSlides}`} />
                        <div className="slide-content">
                            <h3>{slides[totalSlides - 1].title}</h3>
                            <h3>{slides[totalSlides - 1].title2}</h3>
                            <p>{slides[totalSlides - 1].description}</p>
                            <p>{slides[totalSlides - 1].description2}</p>
                            <button>{slides[totalSlides - 1].button}</button>
                            <button>{slides[totalSlides - 1].button2}</button>
                        </div>
                    </div>
                    {slides.map((slide, index) => (
                        <div className="slide" key={index + 1} ref={(el) => slideRefs.current[index + 1] = el}>
                            <img className="bg-image" src={slide.imgSrc} alt={`Image ${index + 1}`} />
                            <div className="slide-content">
                                <h3>{slide.title}</h3>
                                <h3>{slide.title2}</h3>
                                <p>{slide.description}</p>
                                <p>{slide.description2}</p>
                                <button>{slide.button}</button>
                                <button>{slide.button2}</button>
                            </div>
                        </div>
                    ))}
                    <div className="slide" ref={(el) => slideRefs.current[totalSlides + 1] = el}>
                        <img className="bg-image" src={slides[0].imgSrc} alt={`Image 1`} />
                        <div className="slide-content">
                            <h3>{slides[0].title}</h3>
                            <h3>{slides[0].title2}</h3>
                            <p>{slides[0].description}</p>
                            <p>{slides[0].description2}</p>
                            <button>{slides[0].button}</button>
                            <button>{slides[0].button2}</button>
                        </div>
                    </div>
                </div>
                <button className="nav next" onClick={moveToNextSlide}><ChevronRight size={20} absoluteStrokeWidth /></button>
            </div>
        </div>
    );
};

export default Slider;
