import React, { useState, useContext, useRef } from "react";
import { ShoppingCart, User  } from "lucide-react"
import './Navbar.css'
import logo from "../Assets/logo.png";
import cart_icon from "../Assets/cart_icon.png";
import { Link } from "react-router-dom";
import { ShopContext } from "../../Context/ShopContext";    
import nav_dropdown from "../Assets/nav-dropdown.png"

const Navbar = () => {

    const [menu, setMenu] = useState("shop")
    const { getTotalCartItems } = useContext(ShopContext)
    const menuRef = useRef()
    
    const dropdown_toggle = (e) => {
        menuRef.current.classList.toggle('nav-menu-visible')
        e.target.classList.toggle('open')
    }

    return (
        <div className="navbar">
            <div className="nav-logo">
                <img src={logo} alt=""/>
                <p>Duc Thinh</p>
            </div>

            <img className="nav-dropdown" onClick={dropdown_toggle} src={nav_dropdown} alt="" />

            <ul ref={menuRef} className="nav-menu">
                <li onClick={() => {setMenu("home")}}><Link style={{ textDecoration: 'none', color: '#fff', fontSize: '18px' }} to='/'>Home</Link> {menu === "home" ? <hr/> : <></>} </li>
                <li onClick={() => {setMenu("store")}}><Link style={{ textDecoration: 'none', color: '#fff', fontSize: '18px' }} to='/store'>Store</Link> {menu === "store" ? <hr/> : <></>} </li>
                <li onClick={() => {setMenu("news")}}><Link style={{ textDecoration: 'none', color: '#fff', fontSize: '18px' }} to='/news'>News</Link> {menu === "news" ? <hr/> : <></>} </li>
                <li onClick={() => {setMenu("pre-orders")}}><Link style={{ textDecoration: 'none', color: '#fff', fontSize: '18px' }} to='/pre-orders'>Pre-Orders</Link> {menu === "pre-orders" ? <hr/> : <></>} </li>
                <li onClick={() => {setMenu("comming-soon")}}><Link style={{ textDecoration: 'none', color: '#fff', fontSize: '18px' }} to='/comming-soon'>Comming Soon</Link> {menu === "comming-soon" ? <hr/> : <></>} </li>
            </ul>

            <div className="nav-login-cart">
                <Link style={{ textDecoration: 'none' }} to='/login'><button style={{ background: '#8262d2' }}><User style={{width: '25px', height: '25px', lineHeight: '30px'}} /> Login</button></Link>
                {/* <Link to='/cart'><ShoppingCart style={{ width: '35px', height: '35px' }}/></Link> */}
                {/* <div className="nav-cart-count">{getTotalCartItems()}</div> */}
            </div>
        </div>
    )
}


export default Navbar;
