import React, { useState, useEffect, useRef  } from 'react';
import './Slide.jsx';
// import slide from './Slide.jsx';
import styles from './CSS/Slider.css';

const Slider = () => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const sliderWrapperRef = useRef(null);
    const slideRefs = useRef([]);

    const slides = [
    { imgSrc: 'image1.jpg', title: 'Title 1', description: 'Description 1', button: 'Button 1' },
    { imgSrc: 'image2.jpg', title: 'Title 2', description: 'Description 2', button: 'Button 2' },
    { imgSrc: 'image3.jpg', title: 'Title 3', description: 'Description 3', button: 'Button 3' },
    { imgSrc: 'image1.jpg', title: 'Title 1', description: 'Description 1', button: 'Button 1' },
    { imgSrc: 'image2.jpg', title: 'Title 2', description: 'Description 2', button: 'Button 2' },
    { imgSrc: 'image3.jpg', title: 'Title 3', description: 'Description 3', button: 'Button 3' },
    ];

    const moveToNextSlide = () => {
    if (currentIndex >= slides.length / 2) {
        setCurrentIndex(0);
        sliderWrapperRef.current.style.transition = 'none';
        sliderWrapperRef.current.style.transform = 'translateX(0px)';
        setTimeout(() => {
        sliderWrapperRef.current.style.transition = 'transform 0.8s cubic-bezier(0.4, 0.0, 0.2, 1)';
        setCurrentIndex(currentIndex + 1);
        }, 20);
    } else {
        setCurrentIndex(currentIndex + 1);
    }
    };

    const moveToPrevSlide = () => {
    if (currentIndex <= 0) {
        setCurrentIndex(slides.length / 2);
        const slideWidth = slideRefs.current[0].clientWidth;
        sliderWrapperRef.current.style.transition = 'none';
        sliderWrapperRef.current.style.transform = `translateX(-${(slides.length / 2) * slideWidth}px)`;
        setTimeout(() => {
        sliderWrapperRef.current.style.transition = 'transform 0.8s cubic-bezier(0.4, 0.0, 0.2, 1)';
        setCurrentIndex(currentIndex - 1);
        }, 20);
    } else {
        setCurrentIndex(currentIndex - 1);
    }
    };

    useEffect(() => {
    const updateSlider = () => {
        const slideWidth = slideRefs.current[0].clientWidth;
        sliderWrapperRef.current.style.transform = `translateX(-${currentIndex * slideWidth}px)`;
    };

    updateSlider();
    window.addEventListener('resize', updateSlider);

    return () => {
        window.removeEventListener('resize', updateSlider);
    };
    }, [currentIndex]);

    return (
    <div className="slider">
        <button className="nav prev" onClick={moveToPrevSlide}>&lt;</button>
        <div className="slider-wrapper" ref={sliderWrapperRef}>
        {slides.map((slide, index) => (
            <div className="slide" key={index} ref={(el) => slideRefs.current[index] = el}>
            <img className="bg-image" src={slide.imgSrc} alt={`Image ${index + 1}`} />
            <div className="slide-content">
                <h3>{slide.title}</h3>
                <p>{slide.description}</p>
                <button>{slide.button}</button>
            </div>
            </div>
        ))}
        </div>
        <button className="nav next" onClick={moveToNextSlide}>&gt;</button>
    </div>
    );
};


export default Slider;
